import React, { useCallback, useEffect, useState } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodes,
  useEdges,
  addEdge,
  useStoreState,
  useStoreActions,
} from 'react-flow-renderer';

import 'react-flow-renderer/dist/style.css';

const initialNodes = [
  { id: '1', type: 'input', data: { label: 'Input Node' }, position: { x: 250, y: 25 } },
  { id: '2', type: 'default', data: { label: 'Default Node' }, position: { x: 100, y: 125 } },
  { id: '3', type: 'output', data: { label: 'Output Node' }, position: { x: 250, y: 250 } },
];

const initialEdges = [
  { id: 'e1-2', source: '1', target: '2', label: 'Edge from Input to Default' },
  { id: 'e2-3', source: '2', target: '3', label: 'Edge from Default to Output' },
];

const ReactFlowSection = (props) => {
  const [nodes, setNodes] = useNodes();
  const [edges, setEdges] = useEdges();

  useEffect(() => {
    setNodes(initialNodes);
    setEdges(initialEdges);
  }, []);

  const addNode = () => {
    const newNode = {
      id: `${nodes.length + 1}`,
      type: 'default',
      data: { label: `Node ${nodes.length + 1}` },
      position: { x: 250, y: 125 },
    };
    setNodes([...nodes, newNode]);
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow elements={elements}>
        <MiniMap />
        <Controls />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>
      <button onClick={addNode}>Add Node</button>
    </div>
  );
};

export default ReactFlowSection;
